import React, { Component } from 'react';
import './App.css';
import anime from 'animejs/lib/anime.es.js';
import MainDiagram from './svg/MainDiagram';
import { Constants } from './constants';

var HelperFunctions = require('./HelperFunctions');


const ANIMATION_STATE_INITIAL = "READ_OPERATION_INITIAL";
const ANIMATION_STATE_UNSAFE_READ = "ANIMATION_STATE_UNSAFE_READ";
const ANIMATION_STATE_LEADER_RECEIVED_MESSAGE_FROM_CLIENT = "ANIMATION_STATE_LEADER_RECEIVED_MESSAGE_FROM_CLIENT";
const ANIMATION_STATE_LEADER_RECEIVED_ACKS_FROM_FOLLOWERS = "ANIMATION_STATE_LEADER_RECEIVED_ACKS_FROM_FOLLOWERS";
const ANIMATION_STATE_NETWORK_PARTITIONED = "ANIMATION_STATE_NETWORK_PARTITIONED";
const ANIMATION_STATE_NODE_C_PARTITIONED = "ANIMATION_STATE_NODE_C_PARTITIONED";
const ANIMATION_STATE_NODE_A_ELECTED_AS_LEADER = "ANIMATION_STATE_NODE_A_ELECTED_AS_LEADER";
const ANIMATION_STATE_CLIENT_SET_OPERATION = 'ANIMATION_STATE_CLIENT_SET_OPERATION';
const ANIMATION_STATE_POST_PARTITION_NODE_A_RECEIVED_MESSAGE_FROM_CLIENT = "ANIMATION_STATE_POST_PARTITION_NODE_A_RECEIVED_MESSAGE_FROM_CLIENT";
const ANIMATION_STATE_POST_PARTITION_NODE_A_RECEIVED_ACK_FROM_NODE_B = "ANIMATION_STATE_POST_PARTITION_NODE_A_RECEIVED_ACK_FROM_NODE_B";
const ANIMATION_STATE_POST_PARTITION_NODE_A_HAS_SENT_ACK_TO_CLIENT = "ANIMATION_STATE_POST_PARTITION_NODE_A_HAS_SENT_ACK_TO_CLIENT";
const ANIMATION_STATE_POST_PARTITION_CLIENT_HAS_READ_FROM_NODE_C = "ANIMATION_STATE_POST_PARTITION_CLIENT_HAS_READ_FROM_NODE_C";

const SET_VALUE1="V1";
const SET_VALUE2="V2";
function setValueText(value) {
	return HelperFunctions.getSetValueText(value);
}

export class ReadOperationAnimation extends Component {
	constructor(props) {
		super(props);
		this.animationState = ANIMATION_STATE_INITIAL;
		this.state = {
			animationFinished: false,
		}
	}

	componentDidMount() {
		this.mainTextSect = document.getElementById('center-message-text');
	}

	pause() {
	}
	resume() {
	}

	onNext() {
		return new Promise((resolve,reject) => {
			this.onNextInternal(resolve,reject);
		});
	}

	onNextInternal(resolve,reject) {
		switch(this.animationState) {
			case ANIMATION_STATE_INITIAL: {
				//////////////////// initial setup ////////////////////

				// hide all outer circles (TODO: revisit this approach)
				var nodeOuterCircles = document.getElementsByClassName('node-outer-circle');
				for (var i = 0; i < nodeOuterCircles.length; i++) {
					HelperFunctions.hideElement(nodeOuterCircles[i]);
				}
				//////////////////////////////////////////////////////
				this.changeMainText('Let\'s say we have a 3-node raft group.<br />C is the raft leader, all nodes have data <br />k = V1', () => {
					// make Node C the Leader
					var nodeC = document.getElementById('node-c-circle');
					nodeC.classList.add('leader-node');
					HelperFunctions.showElement(document.getElementById('node-a-term-text'));
					HelperFunctions.showElement(document.getElementById('node-b-term-text'));
					HelperFunctions.showElement(document.getElementById('node-c-term-text'));

					HelperFunctions.showElement(document.getElementById('node-a-value-text-rect'));
					HelperFunctions.showElement(document.getElementById('node-b-value-text-rect'));
					HelperFunctions.showElement(document.getElementById('node-c-value-text-rect'));

          this.animationState = ANIMATION_STATE_UNSAFE_READ;
          resolve({
            animationState: this.animationState,
            delay: 100,
          });
				});
				break;
			}
			case ANIMATION_STATE_UNSAFE_READ: {
				this.changeMainText('Unsafe to read from raft leader without majority heartbeats. This sequence explains why.', () => {
					this.animationState = ANIMATION_STATE_NODE_C_PARTITIONED;
					resolve({
						animationState: this.animationState,
						delay: 100,
					});
				});
				break;
			}
			case ANIMATION_STATE_NODE_C_PARTITIONED: {
				this.changeMainText('');
        const altBubbleText = document.getElementById('node-c-message-text-alt');
        HelperFunctions.showElement(document.getElementById('node-c-message-bubble-alt'));

				const contentLine1 = {
					index: 0,
					str: 'Now imagine raft leader C gets\n partitioned from follower\n[but not from client]'
        };

        anime({
          targets: contentLine1,
          index: contentLine1.str.length,
          easing: 'linear',
          duration: 1500,
          update: function() {
            altBubbleText.innerText = contentLine1.str.substr(0, contentLine1.index);
          },
          complete: () => {
            HelperFunctions.partitionNodeC()
            this.animationState = ANIMATION_STATE_NODE_A_ELECTED_AS_LEADER;
            resolve({
              animationState: this.animationState,
              delay: 100,
            });
          }
				});
				break;
			}
			case ANIMATION_STATE_NODE_A_ELECTED_AS_LEADER: {
        HelperFunctions.hideElement(document.getElementById('node-c-message-text-alt'));
        HelperFunctions.hideElement(document.getElementById('node-c-message-bubble-alt'));
        const nodeATextBox = document.getElementById('node-a-message-status');
        HelperFunctions.showElement(document.getElementById('node-a-message-bubble'));
        const content = {
					index: 0,
					str: 'This results in A and B\n electing a new raft\nleader, say A.'
        };

        anime({
          targets: content,
          index: content.str.length,
          easing: 'linear',
          duration: 1300,
          update: function() {
            nodeATextBox.innerText = content.str.substr(0, content.index);
          },
          complete: () => {
            var nodeA = document.getElementById('node-a-circle');
            nodeA.classList.add('leader-node');
            HelperFunctions.setSVGText({targetId: 'node-a-term-text', text: "Term: 2"});
            HelperFunctions.setSVGText({targetId: 'node-b-term-text', text: "Term: 2"});
            HelperFunctions.showElement(document.getElementById('node-a-term-highlight'));
            HelperFunctions.showElement(document.getElementById('node-b-term-highlight'));
            this.animationState = ANIMATION_STATE_CLIENT_SET_OPERATION;
            resolve({
              animationState: this.animationState,
              delay: 1000,
            });
          }
				});
				break;
			}
			case ANIMATION_STATE_CLIENT_SET_OPERATION: {
        /**
         * Update: (monospaced) UPDATE T SET value = V2 WHERE key = k 
         * (Get rid of committed but leave uncommitted and change node
         *  subtext to k: V1, and highlight the value. 
         *  After Node A sends msg to client, show Client: “Update successful!”
         */
        HelperFunctions.hideElement(document.getElementById('node-a-message-status'));
        HelperFunctions.hideElement(document.getElementById('node-a-message-bubble'));
        HelperFunctions.hideElement(document.getElementById('node-a-term-highlight'));
        HelperFunctions.hideElement(document.getElementById('node-b-term-highlight'));
        HelperFunctions.introduceClient();

        const statusElem = document.getElementById('client-message-status');
        const clientContent1 = {
          index: 0,
          str: 'UPDATE T SET'
        }
        const clientContent2 = {
          index: 0,
          str: 'value = V2 WHERE'
        }
        const clientContent3 = {
          index: 0,
          str: ' key = k'
        }

        const statusText1 = document.getElementById('client-message-status-text1');
        const statusText2 = document.getElementById('client-message-status-text2');
        const statusText3 = document.getElementById('client-message-status-text3');
        HelperFunctions.showElement(document.getElementById('client-message-bubble'));
        HelperFunctions.showElement(statusElem);
        anime({
          targets: clientContent1,
          index: clientContent1.str.length,
          easing: 'linear',
          duration: 400,
          update: function() {
            statusText1.textContent = clientContent1.str.substr(0, clientContent1.index);
          },
          complete: () => {
            anime({
              targets: clientContent2,
              index: clientContent2.str.length,
              easing: 'linear',
              duration: 400,
              update: function () {
                statusText2.textContent = clientContent2.str.substr(0, clientContent2.index);
              },
              complete: () => {
                anime({
                  targets: clientContent3,
                  index: clientContent3.str.length,
                  easing: 'linear',
                  duration: 400,
                  update: function () {
                    statusText3.textContent = clientContent3.str.substr(0, clientContent3.index);
                  },
                  complete: () => {   
                    var animation = HelperFunctions.sendLogMessage(Constants.CLIENT_NODE, Constants.NODE_A, false, false, ' ');
                    animation.finished.then(() => {
                      var nodeAToBAnimation = HelperFunctions.sendLogMessage(Constants.NODE_A, Constants.NODE_B, true, false, ' ');
                      nodeAToBAnimation.finished.then(() => {
                        HelperFunctions.hideElement(document.getElementById('client-message-status'));
                        HelperFunctions.hideElement(document.getElementById('client-message-bubble'));
                        HelperFunctions.showElement(document.getElementById('node-a-message-status'));
                        HelperFunctions.showElement(document.getElementById('node-a-message-bubble'));

                        // send commit confirmation back to B
                        HelperFunctions.sendLogMessage(Constants.NODE_A, Constants.NODE_B, false, false, '', true, 600);

                        // notify client as well
                        var messageToClientAnimation = HelperFunctions.sendLogMessage(Constants.NODE_A, Constants.CLIENT_NODE);

                        messageToClientAnimation.finished.then(() => {
                          HelperFunctions.showElement(document.getElementById('client-message-bubble'));
                          HelperFunctions.showElement(document.getElementById('client-message-status-anchor'));
                          document.getElementById('client-message-status-anchor').innerText = 'Update successful!';
                          this.animationState = ANIMATION_STATE_POST_PARTITION_NODE_A_HAS_SENT_ACK_TO_CLIENT;
                          resolve({
                            animationState: this.animationState,
                            delay: 1000,
                          });
                        });
                      });
                    })
                  }
                });
              }
            });
          }
        });
				break;
			}
			case ANIMATION_STATE_POST_PARTITION_NODE_A_HAS_SENT_ACK_TO_CLIENT: {
        HelperFunctions.hideElement(document.getElementById('node-a-message-status'));
        HelperFunctions.hideElement(document.getElementById('node-a-message-bubble'));

        const statusElem = document.getElementById('client-message-status');
        const clientContent = {
          index: 0,
          str: 'Reading from C..'
        }
        const statusText = document.getElementById('client-message-status-text2');

        document.getElementById('client-message-status-text1').textContent = 'Client: ';
        HelperFunctions.showElement(document.getElementById('client-message-bubble'));
        HelperFunctions.showElement(statusElem);
        anime({
          targets: clientContent,
          index: clientContent.str.length,
          easing: 'linear',
          duration: 600,
          update: function() {
            statusText.textContent = clientContent.str.substr(0, clientContent.index);
          },
          complete: () => {
            var animation = HelperFunctions.sendLogMessage(Constants.CLIENT_NODE, Constants.NODE_C, false);

            animation.finished.then(() => {
              this.changeMainText('C thinks it is still the leader and responds with value: ' + SET_VALUE1 + ', which is stale', () => {

                var animation = HelperFunctions.sendLogMessage(Constants.NODE_C, Constants.CLIENT_NODE, false);
                animation.finished.then(() => {
                  HelperFunctions.hideElement(document.getElementById('client-message-status'));
                  HelperFunctions.hideElement(document.getElementById('client-message-bubble'));
                  HelperFunctions.setSVGText({
                    targetId:'client-node-value',
                    text: `Value: ${SET_VALUE1}`,
                    addCSSClass: 'stale-data-text',
                  });
                })
                this.animationState = Constants.ANIMATION_STATE_FINISHED;
                this.setState({ animationFinished: true });
                resolve({
                  animationState: this.animationState,
                  delay: 100,
                });
              });
            });
          }
        });
				break;
			}
			case Constants.ANIMATION_STATE_FINISHED: {
				resolve({
					animationState: this.animationState,
					delay: 100,
				});
				break;
			}
			default:
				console.error('Unrecognized state: ' + this.animationState);
		}
	}

	changeMainText(text, onComplete) {
		HelperFunctions.setTextWithAnimation(this.mainTextSect, text, onComplete);
	}

	render() {
		return(
			<div>
				<div id="main-diagram">
					<MainDiagram/>
				</div>
				<div id="main-text-sect">
				</div>
			</div>
		)
	}
}

export default ReadOperationAnimation;
